#!/bin/bash
mkdir -p ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Ruby cp Ruby.tmLanguage ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Ruby/Ruby.tmLanguage
mkdir -p ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Rails cp Ruby\ on\ Rails.tmLanguage ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Rails/Ruby\ on\ Rails.tmLanguage
mkdir -p ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Color\ Scheme\ -\ Default cp Monokai.tmTheme ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Color\ Scheme\ -\ Default/Monokai.tmTheme
mkdir -p ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/User cp Default.sublime-keymap ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/User/Default.sublime-keymap
mkdir -p ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Ruby cp hash-pair-\(-\).sublime-snippet ~/Library/Application\ Support/Sublime\ Text\ 2/Packages/Ruby/hash-pair-\(-\).sublime-snippet
echo Finished Sublime 2 setup!
